    private _rangeSelectionStrategy: UsaDateRangeSelectionStrategy<D>) {
    this._closeButtonText = 'Close';
  }

  ngOnInit() {
    this._model = this._globalModel;
    usaFocusTrap(this._zone, this._el.nativeElement, this.datePicker.closedStream);
