/** Base class for a datePicker. */
@Directive()
export abstract class UsaDatePickerBase<C extends UsaDatePickerControl<D>, S,
  D = ExtractDateTypeFromSelection<S>> implements UsaDatePickerPanel<C, S, D>, OnDestroy,
  OnChanges {
