  /** The maximum selectable date. */
  _getMaxDate(): D | null {
    return this.datePickerInput && this.datePickerInput.max;
  }

  _getDateFilter(): DateFilterFn<D> {
    return this.datePickerInput && this.datePickerInput.dateFilter;
  }

