import { Component, OnInit } from '@angular/core';
import { ModalDismissReasons, UsaModalService, UsaModalRef } from "@gsa-sam/ngx-uswds";

@Component({
  selector: 'usa-disable-animation',
  templateUrl: './disable-animation.component.html',
  styles: [
  ]
})
export class DisableAnimationComponent {

  closeResult = '';

  modalRef: UsaModalRef;

  constructor(private modalService: UsaModalService) { }

  open(content) {
    this.modalRef = this.modalService.open(content, { animation: false });
    this.modalRef.result.then((result) => {
      this.closeResult = `Closed with: ${result}`;
    }, (reason) => {
      this.closeResult = `Dismissed ${this.getDismissReason(reason)}`;
    });
  }

  close(reason) {
    this.modalRef.close(reason);
  }

  private getDismissReason(reason: any): string {
    if (reason === ModalDismissReasons.ESC) {
      return 'by pressing ESC';
    } else if (reason === ModalDismissReasons.BACKDROP_CLICK) {
      return 'by clicking on a backdrop';
    } else if (reason === ModalDismissReasons.CLOSE_CLICKED) {
      return 'by clicking close button';
    }
    else {
      return `with: ${reason}`;
    }
  }
}

import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { DocumentationDemoList } from '../demo-list';

@Component({
  template: `
    <documentation-widget-demo
      *ngFor="let demo of demos"
      [id]="demo.id"
      [demoTitle]="demo.title"
      [code]="demo.code"
      [markup]="demo.markup"
      [module]="demo.module"
      [path]="demo.path"
      [readme]="demo.readme"
      [component]="component"
      [files]="demo.files"
      [showCode]="demo.showCode"
    >
      <ng-template [ngComponentOutlet]="demo.type"></ng-template>
    </documentation-widget-demo>
  `,
})
export class DocumentationExamplesPage {
  component: string;
  demos = [];

  constructor(route: ActivatedRoute, demoList: DocumentationDemoList) {
    const componentName = (this.component = route.parent.parent.snapshot.url[0].path);
    if (componentName) {
      const demos = demoList.getDemos(componentName);
      if (demos) {
        this.demos = Object.keys(demos).map((id) => {
          return { id, ...demos[id] };
        });
      }
    }
  }
}

