import { ChangeDetectionStrategy, ChangeDetectorRef, Component, EventEmitter, forwardRef, Input, Output } from '@angular/core';
import { NG_VALUE_ACCESSOR } from '@angular/forms';
import { Key } from '../util/key';

let nextId = 0;

@Component({
  selector: 'usa-search',
  templateUrl: './search.component.html',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => UsaSearchComponent),
      multi: true,
    },
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class UsaSearchComponent {

  model: string = '';

  private _onChange = (_: any) => { };
  private _onTouched = () => { };

  @Input() ariaLabel = 'search component'
  @Input() id = `usa-search-${nextId++}`;
  @Input() size: 'big' | 'small';
  @Input() buttonText = 'Search';
  @Input() placeholder = '';
  @Input() name = "search";

  @Output() onBlur: EventEmitter<string> = new EventEmitter(null);

  @Output() searchTextChange: EventEmitter<string> = new EventEmitter(null);

  constructor(public cdr: ChangeDetectorRef) { }

  focusChange(event) {
    this.onBlur.emit(event.target.value)
  }

  onKeydown(event): void {
    if (event.code == Key.Enter) {
      this.model = event.target.value;
      this.updateModel();
      event.preventDefault();
    }
  }

  onValueChange(event) {
    this.searchTextChange.emit(event);
  }

  // Helper method to programatically update the search value to the model
  onSubmit(val, ev) {
    this.model = val;
    this.updateModel();
    ev.preventDefault();
  }

  // Helper method that gets a new instance of the model and notifies ControlValueAccessor that we have a new model for this FormControl (our custom component)
  updateModel() {
    this._onChange(this.model);
  }

  // ControlValueAccessor (and Formly) is trying to update the value of the FormControl (our custom component) programatically
  // If there is a value we will just overwrite items
  // If there is no value we reset the items array to be empty
  writeValue(value: any) {
    if (value) {
      this.model = value;

      this.cdr.markForCheck();
    } else {
      this.model = '';
      this.cdr.markForCheck();
    }
  }

  // ControlValueAccessor hook that lets us call this._onChange(var) to let the form know our variable has changed (in this case model)
  registerOnChange(fn: any): void {
    this._onChange = fn;
  }

  // ControlValueAccessor hook (not used)
  registerOnTouched(fn: any) {
    this._onTouched = fn;
  }
}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { NavigationMode, SidenavModel } from './sidenav.model';


@Component({
  selector: `usa-sidenav`,
  templateUrl: `sidenav.component.html`,
})
export class UsaSidenavComponent implements OnInit {

  NavigationMode = NavigationMode;

  @Input() sidenavContent: SidenavModel[];

  /**
   * Enables navigation items to be toggled. By default all EXTERNAL and INTERNAL links are fully collapsed unless specified in sidenavContent.
   *
   * 'single' will allow only one branch to be expanded at a time. If a new branch is expanded, all open branches will collapse
   *
   * 'multiple' will allow all branches to be expanded independently of each other.
   */
  @Input() expandType: 'single' | 'multiple';

  /**
   * Collapse the children of LABEL by default. Setting to false has no effect on behavior of single-select. This behavior can be overriden on a link by link basis via configuation
   */
  @Input() autoCollapseLabels: boolean = true;

  /**
   * Enables links with a mode of NavigationMode.LABEL to be collapsed, by default they are expanded and locked.
   */
  @Input() enableLabelCollapse = false;

  /**
   * If true, the first child of the first label in the side nav will be selected by default
   */
  @Input() selectFirstLabelChild = false;



  @Output() sidenavClicked = new EventEmitter<SidenavModel>();

  ngOnInit(): void {
    // If collapse is enabled, collapse all children by default. If label, expand to show children and select the first child of the first label
    if (this.expandType) {
      this.sidenavContent.map(link => {
        if (link.mode !== NavigationMode.LABEL) {
          // By default, link will not be expanded to show children. But this can be overridden for a given link based on configuration
          link.collapsed = link.collapsed === undefined ? true : link.collapsed;
        }
        else {
          link.collapsed = link.collapsed === undefined ? !this.autoCollapseLabels && this.expandType !== 'multiple' : link.collapsed;
        }
        if (link.children) {
          this.collapseChildren(link);
        }
      });
    }
  }

  private collapseChildren(link: SidenavModel): void {
    link.children = link.children.map(childLink => {
      childLink.collapsed = true;
      return childLink;
    });
  }

  onSidenavItemClicked(item: SidenavModel): void {

    this.deselectSideNav(this.sidenavContent);
    this.selectSideNav(item, this.sidenavContent);
    this.deactivateChild(this.sidenavContent);
    if (item.children && this.canCollapseLabel(item)) {
      item.collapsed = !item.collapsed;
      this.toggleBasedOnSelected(item.children);
    }
    if (this.expandType === 'single') {

      this.toggleBasedOnSelected(this.sidenavContent);
    }
    this.sidenavClicked.emit(item);
  }

  /**
   *
   * @param link - Link which has been clicked on
   * @returns true if either link is not a label, or it is a label and label collapse is enabled
   */
  private canCollapseLabel(link: SidenavModel): boolean {
    return link.mode === NavigationMode.LABEL ? this.enableLabelCollapse : true;
  }

  private toggleBasedOnSelected(links: SidenavModel[]): void {
    links.forEach(link => {
      link.collapsed = link.selected ? false : true;
      if (link.children) {
        this.toggleBasedOnSelected(link.children);
      }
      return;
    });
  }

  /**
   * Deselects any previously selected sidenav item
   * @param sidenavItems
   */
  private deselectSideNav(sidenavItems: SidenavModel[]): void {
    sidenavItems.forEach(sideNavItem => {
      if (sideNavItem.children) {
        this.deselectSideNav(sideNavItem.children);
      }
      sideNavItem.selected = false;
    });
  }

  /**
   * Selects the clicked sidenav item as we as any parent
   * @param selectedItem
   * @param allNavItems
   */
  private selectSideNav(selectedItem: SidenavModel, allNavItems: SidenavModel[]): boolean {
    for (const item of allNavItems) {

      if (item === selectedItem) {
        item.selected = true;
        return true;
      } else if (item.children) {
        const isChildSelected = this.selectSideNav(selectedItem, item.children);
        if (isChildSelected) {
          item.selected = true;
          return true;
        }
      }
    }
    return false;
  }

  /**
   * If selected item is a grandchild link, the child link needs to not be active per uswds.
   * If selected link is a child link, child link needs to remain selected.
   * @param allNavItems
   */
  private deactivateChild(allNavItems: SidenavModel[]): void {
    const topLevelLink = allNavItems.find(item => item.selected);
    if (topLevelLink.children) {
      const selectedChildLink = topLevelLink.children.find(item => item.selected);
      if (selectedChildLink?.children) {
        const selectedGrandchildLink = selectedChildLink.children.find(item => item.selected);
        if (selectedGrandchildLink) {
          selectedChildLink.selected = false;
        }
      }
    }
  }

  /**
   * When expandType is multiple, expands all collapseable links in sidenav
   */
  public expandAll(): void {
    if (this.expandType === 'multiple') {
      this.sidenavContent.forEach(link => this.expandChildren(link, false));
    }
  }

  /**
   * Collapses all links
   */
  public collapseAll(): void {
    this.sidenavContent.forEach(link => this.expandChildren(link, true));
  }

  private expandChildren(link: SidenavModel, collapsedValue: boolean): void {
    if (link.children) {
      link.collapsed = collapsedValue;
      link.children.forEach(childLink => childLink.children ? this.expandChildren(childLink, collapsedValue) : null);
    }
  }
  private queryStringBuilder(item: SidenavModel): string {
    const ret = [];
    let keys = [];
    if (item.queryParams) {
      keys = Object.keys(item.queryParams);
    }
    for (const d of keys) {
      ret.push(encodeURIComponent(d) + '=' + encodeURIComponent(item.queryParams[d]));
    }
    return ret.join('&');
  }

  /**
   * creates url from provided route and query params
   * @param item - Link to use when building url
   */
  urlBuilder(item: SidenavModel): string {
    let url = item.href;
    const queryParams = this.queryStringBuilder(item);
    if (queryParams) {
      if (url.indexOf('?') === -1) {
        url += '?' + queryParams;
      } else if (url.indexOf('?') === url.length - 1) {
        url += queryParams;
      } else {
        url += '&' + queryParams;
      }
    }
    return url;
  }

}

import { DOCUMENT } from "@angular/common";
import { Component, ElementRef, EventEmitter, Inject, Input, Output } from "@angular/core";

@Component({
  selector: `button[usa-sort]`,
  host: {
    class: 'usa-table__header__button',
    '[attr.aria-label]': 'ariaLabel',
    '(click)': 'onClick($event)',
  },
  template: `
        <svg
          [ngSwitch]="ariaSort"
          class="usa-icon" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24">
          <g *ngSwitchCase="'descending'" class="descending">
            <path d="M17 17L15.59 15.59L12.9999 18.17V2H10.9999V18.17L8.41 15.58L7 17L11.9999 22L17 17Z"></path>
          </g>
          <g *ngSwitchCase="'ascending'" class="ascending">
            <path transform="rotate(180, 12, 12)" d="M17 17L15.59 15.59L12.9999 18.17V2H10.9999V18.17L8.41 15.58L7 17L11.9999 22L17 17Z"></path>
          </g>
          <g *ngSwitchCase="'none'" class="unsorted" fill="transparent">
            <polygon points="15.17 15 13 17.17 13 6.83 15.17 9 16.58 7.59 12 3 7.41 7.59 8.83 9 11 6.83 11 17.17 8.83 15 7.42 16.41 12 21 16.59 16.41 15.17 15"></polygon>
          </g>
        </svg>
  `
})
export class UsaSort {
  @Input() sortFn: (a: any, b: any) => number;

  @Output() sortClicked = new EventEmitter();

  ariaSort: 'ascending' | 'descending' | 'none' = 'none';
  ariaLabel: string;

  private _columnHeaderText: string;

  constructor(
    public _el: ElementRef,
    @Inject(DOCUMENT) private _document
  ) {}

  onClick() {
    this.toggleAriaSort();
    const sortFn = this.sortFn ? this.sortFn : this.defaultSortFunction;
    const eventDetails = {
      sortFn, 
      sortState: this.ariaSort, 
      columnHeaderText: this._columnHeaderText
    };

    let event: CustomEvent;
    if (typeof(Event) === 'function') {
      event = new CustomEvent('sort', {bubbles: true, detail: eventDetails});
    } else {
      // IE11 support
      event = this._document.createEvent('CustomEvent');
      event.initCustomEvent('sort', true, true, eventDetails);
    }

    this._el.nativeElement.dispatchEvent(event);
    this.sortClicked.emit(this.ariaSort);
  }

  setColumnHeader(columnHeaderText: string) {
    this._columnHeaderText = columnHeaderText;
    this.ariaLabel = `Click to sort ${this._columnHeaderText}`;
  }

  toggleAriaSort() {
    this.ariaSort = this.ariaSort === 'none' || this.ariaSort === 'descending' ? 'ascending' : 'descending';
    const sortOrder = this.ariaSort === 'ascending' ? 'in descending order' : 'in ascending order';
    this.ariaLabel = `Click to sort ${this._columnHeaderText} ${this.ariaSort ? sortOrder : ''}`;
  }

  disableSort() {
    this.ariaSort = 'none';
    this.ariaLabel = `Click to sort ${this._columnHeaderText}`;
  }

  private defaultSortFunction(value1: any, value2: any) {
    // if neither value is empty, and if both values are already numbers, compare numerically
    if (
      value1 &&
      value2 &&
      !Number.isNaN(Number(value1)) &&
      !Number.isNaN(Number(value2))
    ) {
      return value1 - value2;
    }

    if (!value1 || !value2) {
      return value1 ? 1 : value2 ? -1 : 0;
    }

    // Otherwise, compare alphabetically based on current user locale
    return value1
      .toString()
      .localeCompare(value2, navigator.language, {
        numeric: true,
        ignorePunctuation: true,
      });
  }
}

import { Component, ContentChild, ElementRef, EventEmitter, Input, OnChanges, Output, SimpleChanges } from '@angular/core';
import { UsaStepIndicatorHeaderComponent } from './step-indicator-header.component';
import { UsaStepIndicatorConfig } from './step-indicator.config';
import { Key, KeyCode, MicrosfotKeys } from '../util/key';
import { getNextItemIndexInList, findLastIndex } from '../util/util';
import { UsaStepIndicatorModel } from './step-indicator.model';

let stepIndicatorId = 0;

@Component({
  selector: 'usa-step-indicator',
  templateUrl: './step-indicator.component.html',
  host: {
    'class': 'usa-step-indicator',
    '[class.usa-step-indicator--no-labels]': 'hideLabels',
    '[class.usa-step-indicator--center]': 'centerLabels && !hideLabels',
    '[class.usa-step-indicator--counters]': 'displayCounters && !smallCounters',
    '[class.usa-step-indicator--counters-sm]': 'smallCounters',

  }
})
export class UsaStepIndicatorComponent implements OnChanges {

  /**
   * Hides labels on each step when set to true
   */
  @Input() hideLabels: boolean = false

  /**
   * Centers label with the step bar when set to true
   */
  @Input() centerLabels: boolean = false

  /**
   * Displays step counter
   */
  @Input() displayCounters: boolean = false

  /**
   * Shrinks size of displayed step counter
   */
  @Input() smallCounters: boolean = false

  /**
   * Whether to place the header above or below the step indicator
   */
  @Input() headerPosition: 'top' | 'bottom' = 'bottom';

  /**
   * Disables the ability to navigate to steps by clicking on the step bar
   */
  @Input() disableStepSelection: boolean = false;


  @Input() id: string = `usa-step-indicator-${stepIndicatorId++}`;

  /**
   * Currently selected step
   */
  @Input() currentStep: number = 0;

  /**
   * All possible steps
   */
  @Input() steps: UsaStepIndicatorModel[] = [];

  @Output() currentStepChange = new EventEmitter<number>();

  constructor(
    config: UsaStepIndicatorConfig,
    private elementRef: ElementRef,
  ) { 
    this.hideLabels = config.hideLabels;
    this.centerLabels = config.centerLabels;
    this.displayCounters = config.displayCounters;
    this.smallCounters = config.smallCounters;
    this.headerPosition = config.headerPosition;
    this.disableStepSelection = config.disableStepSelection;
  }

  ngOnChanges(changes: SimpleChanges) {
    if (changes.currentStep) {
      if (this.currentStep > this.steps.length - 1 || this.currentStep < 0) {
        throw new Error(`Steps in step indicator is out of bounds. 
          Current Step: ${this.currentStep}, Step Range: 0 - ${this.steps.length - 1}`);
      }
    }
  }

  onStepClicked(stepIndex: number, step: any) {
    if (step.disabled || this.disableStepSelection) {
      return;
    }

    this.currentStepChange.emit(stepIndex);
  }

  onKeyDown(event: KeyboardEvent, index: number) {
    const keyPressed = event.key || event.keyCode;
    let nextStepIndex: number;
    switch(keyPressed) {
      case Key.ArrowRight:
      case MicrosfotKeys.ArrowRight:
      case KeyCode.ArrowRight:
        nextStepIndex = getNextItemIndexInList(index, this.steps, 1);
        this._getAllStepListElements()[nextStepIndex].focus();
        event.preventDefault();
        break;
      case Key.ArrowLeft:
      case MicrosfotKeys.ArrowLeft:
      case KeyCode.ArrowLeft:
        nextStepIndex = getNextItemIndexInList(index, this.steps, -1);
        this._getAllStepListElements()[nextStepIndex].focus();
        event.preventDefault();       
        break;
      case Key.Home:
      case MicrosfotKeys.Home:
      case KeyCode.Home:
        nextStepIndex = this.steps.findIndex(step => !step.disabled);
        this._getAllStepListElements()[nextStepIndex].focus();
        event.preventDefault();
        break;
      case Key.End:
      case MicrosfotKeys.End:
      case KeyCode.End:
        nextStepIndex= findLastIndex(this.steps, (step => !step.disabled));
        this._getAllStepListElements()[nextStepIndex].focus();
        event.preventDefault();
        break;
      }
  }

  private _getAllStepListElements() {
    const listElements: NodeList = this.elementRef.nativeElement.querySelectorAll(`#${this.id} .usa-step-indicator__segment`);
    return Array.prototype.slice.call(listElements);
  }

}

