  rowHeader?: string;
  [props: string]: any;
}

import { 
  Component, 
  ElementRef, 
  Input, 
  TemplateRef, 
  ViewChild, 
  forwardRef,
  Output,
  EventEmitter, 
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { Observable } from 'rxjs';
import { FileInputConfig } from './file-input.config';

export interface UploadedFile {
  isLoading?: boolean, 
  imageId: string, 
  file: File
}
@Component({
  selector: 'usa-file-input',
  templateUrl: './file-input.component.html',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => UsaFileInputComponent),
      multi: true,
    },
  ],
})
export class UsaFileInputComponent implements ControlValueAccessor {

  @ViewChild('fileInputEl') fileInputElement: ElementRef;

  @Input() multiple = false;

  @Input() acceptFileType: string = '';

  @Input() id: string;

  @Input() hint: string | TemplateRef<any>;

  @Input() disabled: boolean;

  @Input() selectedFiles: File[] = [];

  @Input() clearFilesOnAdd: boolean;

  @Input() displayFileInfo: boolean;

  @Input() uploadRequest: (file: File) => Observable<any>;

  @Output() selectedFilesChange = new EventEmitter<File[]>();

  @Output() uploadError = new EventEmitter<File>();

  DRAG_OVER_STATE = false;
  ERROR_STATE = false;

  inputFiles: UploadedFile[] = [];

  // Save the callbacks, make sure to have a default so your app
  // doesn't crash when one isn't (yet) registered
  private onChange = (v: any) => {};
  private onTouched = () => {};


  constructor(
    private fileInputConfig: FileInputConfig,
  ) { 
    this.multiple = this.fileInputConfig.multiple;
    this.acceptFileType = this.fileInputConfig.acceptFileType;
    this.clearFilesOnAdd = this.fileInputConfig.clearFilesOnAdd;
    this.displayFileInfo = this.fileInputConfig.displayFileInfo;
    this.uploadRequest = this.fileInputConfig.uploadRequest;
  }

  onDragLeave() {
    this.DRAG_OVER_STATE = false;
  }

  onDragOver() {
    this.DRAG_OVER_STATE = true;
  }

  onFileDrop() {
    this.DRAG_OVER_STATE = false;
  }

  onNewFilesUpload($event) {
    const newFiles: File[] = Array.from($event.target.files);
    this.fileInputElement.nativeElement.value = null;

    if (newFiles.length === 0) {
      return;
    }

    // Validate new files match expected types
    let areValidFiles = this.validateFileType(newFiles);

    if (areValidFiles) {
      this.ERROR_STATE = false;
    } else {
      this.ERROR_STATE = true;
      this.selectedFiles = [];
      this.inputFiles = [];
      return;
    }

    // Clear current files OR append to existing based on user config
    if (!this.multiple || this.clearFilesOnAdd) {
      this.inputFiles = [];
      this.selectedFiles = newFiles;
    } else {
      this.selectedFiles = this.selectedFiles.concat(newFiles);
    }

    // Read file data and add laoding / preview states
    newFiles.forEach(file => {
      const imageId = this.getImageId(file, this.inputFiles);
      const inputFile = {
        isLoading: true, imageId, file
      };
      this.inputFiles.push(inputFile);
    });

    this.selectedFilesChange.emit(this.selectedFiles);

    // Notify Angular to update its model
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  removeFile(file: File) {
    const index = this.selectedFiles.indexOf(file);
    let inputFileIndex = -1;
    this.inputFiles.forEach((inputFile, index) => {
      if (inputFile.file === file) {
        inputFileIndex = index;
      }
    });

    if (index === -1 || inputFileIndex === -1) {
      return;
    }

    this.selectedFiles.splice(index, 1);
    this.selectedFilesChange.emit(this.selectedFiles);
    this.writeValue(this.selectedFiles);
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  clearFiles() {
    this.selectedFiles = [];
    this.selectedFilesChange.emit(this.selectedFiles);
    this.writeValue(this.selectedFiles);
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  onUploadError(file: File) {
    this.uploadError.emit(file);
  }

  trackLoadedFilesBy(index: number, item: UploadedFile) {
    return item.imageId;
  }

  registerOnChange(fn: any): void {
    this.onChange = fn;
  }
  registerOnTouched(fn: any): void {
    this.onTouched = fn;
  }

  writeValue(files: File[]): void {
    if (this.disabled || !files) {
      return;
    }
    let areValidFiles = this.validateFileType(files);

    if (areValidFiles) {
      this.ERROR_STATE = false;
    } else {
      this.ERROR_STATE = true;
      return;
    }

    this.inputFiles = [];
    this.selectedFiles = files;
    // Read file data and add laoding / preview states
    files.forEach(file => {
      const imageId = this.getImageId(file, this.inputFiles);
      const inputFile = {
        isLoading: true, imageId, file
      };
      this.inputFiles.push(inputFile);
    });
  }

  setDisabledState(isDisabled: boolean) {
    this.disabled = isDisabled;
  }

  private validateFileType(files: File[]) {
    if (!this.acceptFileType) {
      return true;
    }

    const acceptedFiles = this.acceptFileType.split(',');

    for(let i = 0; i < files.length; i++) {
      const isValidFileType = acceptedFiles.some(acceptedFileType => {
        const endsWithFileType = new RegExp(acceptedFileType + '$', 'i').test(files[i].name);
        return endsWithFileType || files[i].type.includes(acceptedFileType.replace(/\*/g, ""))
      });

      if (!isValidFileType) {
        return false;
      }
    }

    return true;
  }



  /**
   * Given a file and list of pre-uploaded files, generates and returns an id for the file.
   * If file already exists in pre-uploaded files list, then the id will simply be appended with
   * a counter to ensure uniqueness
   * @param file - File to generate id for
   * @param preExistingFiles - List of files that are uploaded and already have an id
   * @returns An id for the image
   */
  private getImageId(file: File, preUploadedFiles: UploadedFile[]) {
    let imageId = this._makeSafeForID(file.name);

    imageId = `a${imageId}`;
    let numExisting = 0;
    preUploadedFiles.forEach(file => {
      if (file.imageId === imageId) {
        numExisting++;
      }
    });

    if (numExisting > 0) {
      imageId = `${imageId}${numExisting}`;
    }
    return imageId;
  }

  /**
   * Creates an ID name for each file that strips all invalid characters.
   * @param {string} name - name of the file added to file input
   * @returns {string} same characters as the name with invalid chars removed
   */
  private _makeSafeForID(name: string) {
    return name.replace(/[^a-z0-9]/g, function replaceName(s) {
      const c = s.charCodeAt(0);
      if (c === 32) return "-";
      if (c >= 65 && c <= 90) return `img_${s.toLowerCase()}`;
      return `__000${(c.toString(16)).slice(-4)}`;
    });
  }
}

import { 
  AfterContentChecked, 
  ContentChildren, 
  Directive, 
  EventEmitter, 
  Input, 
  Output, 
  QueryList, 
  TemplateRef } from "@angular/core";

let nextId = 0;


/**
 * An event emitted right before toggling an accordion panel.
 */
 export interface UsaAccordionChangeEvent {
  /**
   * The id of the accordion panel being toggled.
   */
  panelId: string;

  /**
   * The next state of the panel.
   *
   * `true` if it will be opened, `false` if closed.
   */
  nextState: boolean;

  /**
   * Calling this function will prevent panel toggling.
   */
  preventDefault: () => void;
}

/**
 * A directive that wraps the accordion panel content.
 */
@Directive({ selector: 'ng-template[UsaAccordionContent]' })
export class UsaAccordionContent {
  constructor(public templateRef: TemplateRef<any>) { }
}

/**
 * A directive that wraps the accordion header content.
 */
@Directive({ 
  selector: 'ng-template[UsaAccordionHeader]',
})
export class UsaAccordionHeader {
  constructor(public templateRef: TemplateRef<any>) { }
}

/**
 * A directive that wraps an individual accordion panel with title and collapsible content.
 */
@Directive({ selector: 'usa-accordion-item' })
export class UsaAccordionItem implements AfterContentChecked {
  /**
   *  If `true`, the panel is disabled an can't be toggled.
   */
  @Input() disabled = false;

  /**
   *  An optional id for the panel that must be unique on the page.
   *
   *  If not provided, it will be auto-generated in the `usa-panel-xxx` format.
   */
  @Input() id = `usa-accordion-item-${nextId++}`;

  /** Defines whether the accordion is in expanded or collapsed state */
  @Input() expanded = false;

  /** A flag to specified that the transition panel classes have been initialized */
  initClassDone = false;

  /**
   *  The panel header.
   */
  @Input() header: string;


  /**
   * An optional class applied to the accordion card element that wraps both panel title and content.
   *
   * @since 5.3.0
   */
  @Input() cardClass: string;

  /**
   * Defines the aria label for accordion header button. Generally if the header is a text, then this is
   * not needed as screen readers can announce the text. However, if the header is a custom template
   * with items that screen readers cannot compute (icons / images), then having a descriptive label can
   * help
   */
  @Input() ariaLabel: string;

  /**
   * An event emitted when the panel is shown, after the transition. It has no payload.
   *
   * @since 8.0.0
   */
  @Output() shown = new EventEmitter<void>();

  /**
   * An event emitted when the panel is hidden, after the transition. It has no payload.
   *
   * @since 8.0.0
   */
  @Output() hidden = new EventEmitter<void>();


  /** Provided template for Accordion header */
  headerTpl: UsaAccordionHeader;

  /** Provided template for Accordion content */
  contentTpl: UsaAccordionContent;

  @ContentChildren(UsaAccordionHeader, { descendants: false }) headerTpls: QueryList<UsaAccordionHeader>;
  @ContentChildren(UsaAccordionContent, { descendants: false }) contentTpls: QueryList<UsaAccordionContent>;

  constructor() {}


  ngAfterContentChecked() {
    // We are using @ContentChildren instead of @ContentChild as in the Angular version being used
    // only @ContentChildren allows us to specify the {descendants: false} option.
    // Without {descendants: false} there are issues when accordions are wrapped inside accordions
    this.headerTpl = this.headerTpls.first;
    this.contentTpl = this.contentTpls.first;
  }
}

import {
  ChangeDetectionStrategy,
  Component,
  ElementRef,
  EventEmitter,
  Input,
  Output,
  NgZone,
  OnChanges,
  SimpleChanges,
  OnDestroy,
} from '@angular/core';
import { take } from 'rxjs/operators';

/** Extra CSS classes that can be associated with a calendar cell. */
export type UsaCalendarCellCssClasses = string | string[] | Set<string> | { [key: string]: any };

/** Function that can generate the extra classes that should be added to a calendar cell. */
export type UsaCalendarCellClassFunction<D> =
  (date: D, view: 'month' | 'year' | 'multi-year') => UsaCalendarCellCssClasses;

/**
 * An internal class that represents the data corresponding to a single calendar cell.
 * @docs-private
 */
export class UsaCalendarCell<D = any> {
  constructor(public value: number,
    public displayValue: string,
    public ariaLabel: string,
    public enabled: boolean,
    public cssClasses: UsaCalendarCellCssClasses = {},
    public compareValue = value,
    public rawValue?: D) { }
}

/** Event emitted when a date inside the calendar is triggered as a result of a user action. */
export interface UsaCalendarUserEvent<D> {
  value: D;
  event: Event;
}

/**
 * An internal component used to display calendar data in a table.
 * @docs-private
 */
@Component({
  selector: '[usa-calendar-body]',
  templateUrl: './calendar-body.html',
  exportAs: 'usaCalendarBody',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class UsaCalendarBody implements OnChanges, OnDestroy {
  /**
   * Used to skip the next focus event when rendering the preview range.
   * We need a flag like this, because some browsers fire focus events asynchronously.
   */
  private _skipNextFocus: boolean;

  @Input() view: 'month' | 'year' | 'multi-year';

  /** The label for the table. (e.g. "Jan 2017"). */
  @Input() label: string;

  /** The cells to display in the table. */
  @Input() rows: UsaCalendarCell[][];

  @Input() prevMonthRow: UsaCalendarCell[];

  @Input() nextMonthRow: UsaCalendarCell[];

  /** The value in the table that corresponds to today. */
  @Input() todayValue: number;

  /** Start value of the selected date range. */
  @Input() startValue: number;

  /** End value of the selected date range. */
  @Input() endValue: number;

  /** The minimum number of free cells needed to fit the label in the first row. */
  @Input() labelMinRequiredCells: number;

  /** The number of columns in the table. */
  @Input() numCols: number = 7;

  /** The cell number of the active cell in the table. */
  @Input() activeCell: number = 0;

  /** Whether a range is being selected. */
  @Input() isRange: boolean = false;

  /**
   * The aspect ratio (width / height) to use for the cells in the table. This aspect ratio will be
   * maintained even as the table resizes.
   */
  @Input() cellAspectRatio: number = 1;

  /** Start of the comparison range. */
  @Input() comparisonStart: number | null;

  /** End of the comparison range. */
  @Input() comparisonEnd: number | null;

  /** Start of the preview range. */
  @Input() previewStart: number | null = null;

  /** End of the preview range. */
  @Input() previewEnd: number | null = null;

  /** Emits when a new value is selected. */
  @Output() readonly selectedValueChange = new EventEmitter<UsaCalendarUserEvent<number>>();

  /** Emits when the preview has changed as a result of a user action. */
  @Output() readonly previewChange =
    new EventEmitter<UsaCalendarUserEvent<UsaCalendarCell | null>>();

  /** The number of blank cells to put at the beginning for the first row. */
  _firstRowOffset: number;

  /** Padding for the individual date cells. */
  _cellPadding: string;

  /** Width of an individual cell. */
  _cellWidth: string;

  constructor(private _elementRef: ElementRef<HTMLElement>, private _ngZone: NgZone) {
    _ngZone.runOutsideAngular(() => {
      const element = _elementRef.nativeElement;
      element.addEventListener('mouseenter', this._enterHandler, true);
      element.addEventListener('focus', this._enterHandler, true);
      element.addEventListener('mouseleave', this._leaveHandler, true);
      element.addEventListener('blur', this._leaveHandler, true);
    });
