import {
  Component, ContentChildren,
  Input, QueryList, ElementRef, AfterContentChecked,
  EventEmitter, Output, Renderer2, Directive, Host, Optional
} from '@angular/core';
import { Key, KeyCode, MicrosfotKeys } from '../util/key';
import { isString, findLast, getNextItemInList } from '../util/util';
import { UsaAccordionItem, UsaAccordionChangeEvent } from './accordion-items';
import { UsaAccordionConfig } from './accordion.config';
import { AnimationEvent } from '@angular/animations';
import { UsaExpansionAnimations } from './accordion-animations';

@Component({
  selector: 'usa-accordion',
  exportAs: 'usaAccordion',
  templateUrl: './accordion.component.html',
  animations: [UsaExpansionAnimations.bodyExpansion],
  host: {
    'class': 'usa-accordion',
    '[class.usa-accordion--bordered]': 'bordered',
    '[attr.aria-multiselectable]': 'singleSelect ? true : undefined',
  },
})
export class UsaAccordionComponent implements AfterContentChecked  {

  @ContentChildren(UsaAccordionItem) panels: QueryList<UsaAccordionItem>;

  /**
   * If `true`, accordion will be animated.
   * @default true
   */
  @Input() animation: boolean = true;

  /**
   * An array or comma separated strings of panel ids that should be opened **initially**.
   *
   * For subsequent changes use methods like `expand()`, `collapse()`, etc. and
   * the `(panelChange)` event.
   */
  @Input() activeIds: string[] = [];

  /**
   *  If `true`, only one panel could be opened at a time.
   *
   *  Opening a new panel will close others.
   */
  @Input() singleSelect: boolean;

  /**
   * Type of panels.
   *
   * Bootstrap provides styles for the following types: `'success'`, `'info'`, `'warning'`, `'danger'`, `'primary'`,
   * `'secondary'`, `'light'` and `'dark'`.
   */
  @Input() bordered: boolean;

  /**
   * Heading level to use for accordion headers - possible inputs are anywhere from heading level 2 to 6.
   */
  @Input() headerLevel: 2 | 3 | 4 | 5 | 6 = 4;

  /**
   * Event emitted right before the panel toggle happens.
   */
  @Output() panelChange = new EventEmitter<UsaAccordionChangeEvent>();

  /**
   * An event emitted when the expanding animation is finished on the panel. The payload is the panel id.
   */
  @Output() shown = new EventEmitter<string>();

  /**
   * An event emitted when the collapsing animation is finished on the panel, and before the panel element is removed.
   * The payload is the panel id.
   *
   * @since 8.0.0
   */
  @Output() hidden = new EventEmitter<string>();

  constructor(
      config: UsaAccordionConfig,
      private _element: ElementRef,
      private _renderer: Renderer2
    ) {
    this.animation = config.animation;
    this.bordered = config.bordered;
    this.singleSelect = config.singleSelect;
    this.headerLevel = config.headerLevel;
  }

  /**
   * Checks if a panel with a given id is expanded.
   */
  isExpanded(panelId: string): boolean { return this.activeIds.indexOf(panelId) > -1; }

  /**
   * Expands a panel with a given id.
   *
   * Has no effect if the panel is already expanded or disabled.
   */
  expand(panelId: string): void { this._changeOpenState(this._findPanelById(panelId), true); }

  /**
   * Expands all panels, if `[closeOthers]` is `false`.
   *
   * If `[closeOthers]` is `true`, it will expand the first panel, unless there is already a panel opened.
   */
  expandAll(): void {
    if (this.singleSelect) {
      if (this.activeIds.length === 0 && this.panels.length) {
        this._changeOpenState(this.panels.first, true);
      }
    } else {
      this.panels.forEach(panel => this._changeOpenState(panel, true));
    }
  }

  /**
   * Collapses a panel with the given id.
   *
   * Has no effect if the panel is already collapsed or disabled.
   */
  collapse(panelId: string) { this._changeOpenState(this._findPanelById(panelId), false); }

  /**
   * Collapses all opened panels.
   */
  collapseAll() {
    this.panels.forEach((panel) => { this._changeOpenState(panel, false); });
  }

  /**
   * Toggles a panel with the given id.
   *
   * Has no effect if the panel is disabled.
   */
  toggle(panelId: string) {
    const panel = this._findPanelById(panelId);
    if (panel) {
      this._changeOpenState(panel, !panel.expanded);
    }
  }

  /** Gets the expanded state string. */
  _getExpandedState(panel: UsaAccordionItem) {
    return panel.expanded ? 'expanded' : 'collapsed';
  }

  ngAfterContentChecked() {
    // active id updates
    if (isString(this.activeIds)) {
      this.activeIds = this.activeIds.split(/\s*,\s*/);
    }

    // update panels open states
    this.panels.forEach(panel => {
      panel.expanded = panel.expanded || (!panel.disabled && this.activeIds.indexOf(panel.id) > -1);
    });

    // closeOthers updates
    if (this.activeIds.length > 1 && this.singleSelect) {
      this._closeOthers(this.activeIds[0]);
      this._updateActiveIds();
    }
  }

  onKeyDown($event: KeyboardEvent, panel: UsaAccordionItem) {
    const keyPressed = $event.key || $event.keyCode;
    switch(keyPressed) {
      case Key.ArrowDown:
      case MicrosfotKeys.ArrowDown:
      case KeyCode.ArrowDown:
        this._getPanelElementHeaderButton(
          this._getNextAccordion(panel, 1).id
        ).focus();
        $event.preventDefault();
        break;
      case Key.ArrowUp:
      case MicrosfotKeys.ArrowUp:
      case KeyCode.ArrowUp:
        this._getPanelElementHeaderButton(
          this._getNextAccordion(panel, -1).id
        ).focus();
        $event.preventDefault();
        break;
      case Key.Home:
      case MicrosfotKeys.Home:
      case KeyCode.Home:
        const firstPanel = this.panels.find(panel => !panel.disabled);
        if (firstPanel) {
          this._getPanelElementHeaderButton(firstPanel.id).focus();
        }
        $event.preventDefault();
        break;
      case Key.End:
      case MicrosfotKeys.End:
      case KeyCode.End:
        const lastFocusablePanel = findLast(this.panels.toArray(), (panel => !panel.disabled));
        if (lastFocusablePanel) {
          this._getPanelElementHeaderButton(lastFocusablePanel.id).focus();
        }
        $event.preventDefault();
        break;
    }
  }

  onBodyExpansionStart(event: AnimationEvent, panel: HTMLDivElement) {
    if (event.fromState === 'collapsed' && event.toState === 'expanded') {
      this._renderer.removeStyle(panel, 'display');
    }
  }

  onBodyExpansionEnd(event: AnimationEvent, panel: HTMLDivElement) {
    if (event.fromState !== 'void') {
      if (event.toState === 'expanded') {
        this.shown.emit(panel.id);
      } else if (event.toState === 'collapsed') {
        this._renderer.setStyle(panel, 'display', 'none');
        this.hidden.emit(panel.id);
      }
    }
  }

  private _getNextAccordion(currentPanel: UsaAccordionItem, delta: 1 | -1) {
    const allPanels = this.panels.toArray();
    const currentIndex = allPanels.indexOf(currentPanel);
    const nextPanel = getNextItemInList(currentIndex, allPanels, delta);
    return nextPanel
  }

  private _changeOpenState(panel: UsaAccordionItem | null, nextState: boolean) {
    if (panel != null && !panel.disabled) {
      let defaultPrevented = false;

      this.panelChange.emit(
          {panelId: panel.id, nextState: nextState, preventDefault: () => { defaultPrevented = true; }});

      if (!defaultPrevented) {
        panel.expanded = nextState;

        if (nextState && this.singleSelect) {
          this._closeOthers(panel.id);
        }
        this._updateActiveIds();
      }
    }
  }

  private _closeOthers(panelId: string) {
    this.panels.forEach(panel => {
      if (panel.id !== panelId && panel.expanded) {
        panel.expanded = false;
      }
    });
  }

  private _findPanelById(panelId: string): UsaAccordionItem | null { return this.panels.find(p => p.id === panelId) || null; }

  private _updateActiveIds() {
    this.activeIds = this.panels.filter(panel => panel.expanded && !panel.disabled).map(panel => panel.id);
  }

  private _getPanelElementHeaderButton(panelId: string): HTMLElement | null {
    return this._element.nativeElement.querySelector('#' + panelId + '-header button');
  }
}

@Directive({
  selector: 'button[UsaAccordionToggle]',
  host: {
    'type': 'button',
    '[disabled]': 'panel.disabled',
    'class': 'usa-accordion__button',
    '[class.collapsed]': '!panel.expanded',
    '[attr.aria-expanded]': 'panel.expanded',
    '[attr.aria-controls]': 'panel.expanded ? panel.id : undefined',
    '[attr.aria-disabled]': 'panel.disabled && panel.expanded ? true : undefined',
    '[attr.aria-label]': 'panel.ariaLabel',
    '(click)': 'accordion.toggle(panel.id)',
  }
})
export class UsaAccordionToggle {
  static ngAcceptInputType_UsaAccordionToggle: UsaAccordionItem | '';

  @Input()
  set UsaAccordionToggle(panel: UsaAccordionItem) {
    if (panel) {
      this.panel = panel;
    }
  }

  constructor(public accordion: UsaAccordionComponent, @Optional() @Host() public panel: UsaAccordionItem) { }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChild,
  Directive,
  EventEmitter,
  Input,
  OnChanges,
  OnInit,
  Output,
  SimpleChanges,
  TemplateRef,
  ViewEncapsulation,
} from '@angular/core';
import { UsaNavigationLink } from '../util/navigation';

/**
 * Selector for custom link template for displaying breadcrumbs.
 * Usage:
 * <usa-breadcrumb [items]="items">
 *  <a *usaBreadcrumbLinkTemplate="let breadcrumb" href="myHref">{{breadcrumb.text}}</a>
 * </usa-breadcrumbs>
 */
@Directive({
  selector: `[usaBreadcrumbLinkTemplate]`,
})
export class UsaBreadcrumbLinkTemplate {
  constructor(public templateRef: TemplateRef<any>) {}
}

@Component({
  selector: 'usa-breadcrumb',
  templateUrl: './breadcrumb.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
  styleUrls: ['./breadcrumb.component.scss'],
  encapsulation: ViewEncapsulation.None,
})
export class UsaBreadcrumbComponent implements OnInit, OnChanges {
  @ContentChild(UsaBreadcrumbLinkTemplate)
  breadcrumbLinkTemplate: UsaBreadcrumbLinkTemplate;

  /**
   * Toggles whether to wrap breadcrumb trail into new line on responsive
   * view or to truncate breadcrumb text. Value of true indicates breadcrumb
   * will wrap to new line, false will allow text to truncate
   * @default false
   */
  @Input() wrap: boolean = false;

  /**
   * Hides breadcrumbs when there is only one item to show for
   * breadcrumb trail. This occurs whenever the user is on the root
   * level page of the breadcrumb trail
   */
  @Input() hideSingleCrumb: boolean = false;

  /** List of breadcrumb items */
  @Input() items: UsaNavigationLink[];

  @Output() selected = new EventEmitter<UsaNavigationLink>();

  _displayedCrumbs: UsaNavigationLink[];
  _showBreadcrumbs: boolean = true;
  _selectedBreadcrumb: UsaNavigationLink;

  constructor(
    /**
     * For public component usage if parents ever want to programatically
     * invoke change detection on component
     */
    public cdr: ChangeDetectorRef
  ) {}

  /**
   * Initialize internal data within the component
   */
  ngOnInit(): void {
    this.updateShownCrumbs();
  }

  /**
   * Update data when relevant input reference changes
   */
  ngOnChanges(changes: SimpleChanges) {
    if (!changes.items && !changes.hideSingleCrumb) return;

    if (this._selectedBreadcrumb) {
      this._selectedBreadcrumb.selected = false;
    }

    this.updateShownCrumbs();
  }

  /**
   * Called whenever we need to update selected breadcrum to
   * another item. Normally occurs if user clicks on the breadcrum
   * @param breadcrumb
   */
  updateSelectedBreadcrumb(breadcrumb: UsaNavigationLink) {
    this._selectedBreadcrumb.selected = false;
    breadcrumb.selected = true;
    this.updateShownCrumbs();
    this.selected.emit(breadcrumb);
  }

  /**
   * Internal function to update the list of breadcrumbs currently shown as well
   * as other component fields
   */
  private updateShownCrumbs() {
    if (!this.items || !this.items.length) {
      this._showBreadcrumbs = false;
      return;
    }

    let truncateIndex = this.items.findIndex(
      (breadcrumb) => breadcrumb.selected
    );

    if (truncateIndex < 0) {
      truncateIndex = this.items.length - 1;
      this.items[truncateIndex].selected = true;
    }

    this._selectedBreadcrumb = this.items[truncateIndex];

    if (truncateIndex === 0 && this.hideSingleCrumb) {
      this._showBreadcrumbs = false;
    } else {
      this._showBreadcrumbs = true;
    }

    this._displayedCrumbs = this.items.slice(0, truncateIndex);
  }
}

import { Directive, ElementRef, Input, NgZone, OnDestroy, OnInit, Optional, Renderer2 } from '@angular/core';
import { NgControl } from '@angular/forms';
import { Subscription } from 'rxjs';

declare var ResizeObserver;

let nextId = 0;
@Directive({
  selector: '[usaCharacterCount]',
})
export class UsaCharacterCountDirective implements OnInit, OnDestroy {

  /** Directive's main input - max length of characters allowed */
  @Input() usaCharacterCount: number;

  /** 
   * Id to use for character count message text. Will be auto-generated if not passed in
   */
  @Input() messageId = `usa-character-count-${nextId++}`;

  private readonly MESSAGE_CLASS = `usa-character-count__message`;
  private readonly MESSAGE_INVALID_CLASS = `usa-character-count__message--invalid`;

  /** 
   * Subscription to form control's value changes if one exists
   * Allows us to unsubscribe during cleanup
   */
  valueSubscription = new Subscription();

  /** Reference to character count text being displayed to users */
  characterCounterText = '';

  /**
   * Watch for resize event changes to the host element. We want to keep the character
   * counter's width attribute in line with host's width for resizable input
   */
  private _inputResizeObserver;

  /**
   * Reference to character counter message element. This will be created during init if
   * one with provided messageId does not already exist as sibling.
   */
  private _messageElement: HTMLSpanElement;

  constructor(
    private element: ElementRef,
    private renderer2: Renderer2,
    private zone: NgZone,
    @Optional() private control: NgControl
  ) { }

  ngOnInit() {
    if (!this.usaCharacterCount) {
      return;
    }

    if (this.control) {
      this.valueSubscription = this.control.valueChanges.subscribe(($event) => this.inputEventHandler($event));
    } else {
      this.element.nativeElement.addEventListener('input', this.inputEventHandler.bind(this));
    }

    // Get reference to character counter's message element
    this._messageElement = this.getMessageElement();
    this.setCharacterCountText(0, this.usaCharacterCount);


    /** Watch for input field's resize event so we can keep character counter text aligned with input */
    this._inputResizeObserver = new ResizeObserver((change) => {
      // Run renderer inside angular's zone so that we can ensure change detection will pick it up
      this.zone.run(() => {
        this._messageElement.style.width = `${change[0].borderBoxSize[0].inlineSize}px`;
      })
    });

    this._inputResizeObserver.observe(this.element.nativeElement);
  }

  ngOnDestroy() {
    /** Clean Up */
    this.valueSubscription.unsubscribe();
    this.element.nativeElement.removeEventListener('input', this.inputEventHandler);
    if (this._inputResizeObserver) {
      this._inputResizeObserver.unobserve(this.element.nativeElement);
    }
  }

  /** Gets reference to span element that will be used to display character counter text. If one does not exists, it will be created
   * and attached as sibling to host form element
   */
  private getMessageElement() {
    let messageElement: HTMLSpanElement = this.element.nativeElement.parentElement.querySelector(`.${this.MESSAGE_CLASS}#${this.messageId}`);
    if (messageElement) {
      return messageElement;
    }

    messageElement = this.renderer2.createElement('span');
    messageElement.classList.add('usa-hint', this.MESSAGE_CLASS);
    messageElement.setAttribute('aria-live', 'polite');
    messageElement.setAttribute('id', this.messageId);
    messageElement.style.width = `${this.element.nativeElement.offsetWidth}px`;
    (this.element.nativeElement.parentElement as HTMLElement).insertBefore(messageElement, this.element.nativeElement.nextSibling);
    this._messageElement = messageElement;

    return messageElement;
  }

  private inputEventHandler($event: InputEvent | string) {
    let value;
    if ($event instanceof InputEvent) {
      value = ($event.target as any).value;
    } else {
      value = $event;
    }

    if (value === undefined || value === null) {
      return;
    }

    this.setCharacterCountText(value.length, this.usaCharacterCount);
  }

  /**
   * Sets character counter message based on current number of characters.
   * The message is appended as sibling element to the element this character count directive
   * is attached through reference of shared parent element.
   * @param currentLength 
   */
  private setCharacterCountText(currentLength: number, maxLength: number) {

    // Get message to display for amount of characters left
    this.characterCounterText = this.generateCharacterCounterText(currentLength, maxLength);

    // Add error css if over limit
    const isOverLimit = currentLength && currentLength > maxLength;
    this._messageElement.classList.toggle(this.MESSAGE_INVALID_CLASS, isOverLimit);

    this._messageElement.innerHTML = this.characterCounterText;
  }

  /**
   * Follows logic from USWDS' character counter's JS file so that messaging is consistent
   * @param currentLength
   * @param maxLength 
   * @returns - string message to display based on current characters and max characters allowed
   */
  private generateCharacterCounterText(currentLength: number, maxLength: number) {
    const isOverLimit = currentLength && currentLength > maxLength;

    if (currentLength === 0) {
      return `${maxLength} characters allowed`;
    } else {
      const difference = Math.abs(maxLength - currentLength);
      const characters = `character${difference === 1 ? "" : "s"}`;
      const guidance = isOverLimit ? "over limit" : "left";

      return `${difference} ${characters} ${guidance}`;
    }
  }
}

import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Directive, ElementRef, EventEmitter, forwardRef, Input, Output, ViewChild } from '@angular/core';
