import { AfterViewInit, Directive, ElementRef, Input, Renderer2, TemplateRef, ViewContainerRef } from '@angular/core';

@Directive({
  selector: 'input[prefix], input[suffix]'
})
export class UsaInputAffixDirective implements AfterViewInit {

  @Input() prefix: TemplateRef<any> | string;
  @Input() suffix: TemplateRef<any> | string;

  constructor(
    private el: ElementRef,
    private renderer: Renderer2,
    private viewContainer: ViewContainerRef) {
  }

  ngAfterViewInit(){

    const parent = this.renderer.parentNode(this.el.nativeElement);

    //Need next sibling to ensure generated usa-input-group is inserted into the same place on the page and input is expected to be
    const nextSibling = this.renderer.nextSibling(this.el.nativeElement);

    let prefixElement, suffixElement;
    if(this.prefix){
      prefixElement = this.handleAffix(this.prefix, 'usa-input-prefix')
    }
    if(this.suffix){
      suffixElement = this.handleAffix(this.suffix, 'usa-input-suffix')
    }
    // Return early if neither prefix nor suffix provided
    if(!this.prefix && !this.suffix){
      return;
    }

    const inputGroupDiv = document.createElement('div');
    this.renderer.addClass(inputGroupDiv,'usa-input-group')

    // Apply input class in case only directive was applied
    this.renderer.addClass(this.el.nativeElement, 'usa-input')

    if(this.prefix){
      this.renderer.appendChild(inputGroupDiv, prefixElement);
    }
    this.renderer.appendChild(inputGroupDiv, this.el.nativeElement);
    if(this.suffix){
      this.renderer.appendChild(inputGroupDiv, suffixElement);
    }

    this.renderer.insertBefore(parent, inputGroupDiv, nextSibling)

  }

  /**
   *
   * @param affix Template or string to be displayed as prefix or suffix.
   * @param affixClass USWDS class to apply to element
   */
  handleAffix(affix: TemplateRef<any> | string, affixClass: string){
    let affixView, affixElement;
    if(affix instanceof TemplateRef){
      affixView = this.viewContainer.createEmbeddedView(affix);
      affixElement = affixView.rootNodes[0];
    }

    // If text passed into template, need to wrap in element for USWDS CSS to kick in.
    if(affixView?.rootNodes[0].nodeName === '#text'){
      const div = document.createElement('div');
      this.renderer.appendChild(div, affixElement);
      affixElement = div;
    // If string passed in, insert into element for CSS to apply to
    } else if(typeof affix === 'string'){
      const div = document.createElement('div');
      div.innerText = affix;
      affixElement = div;
    }
    this.renderer.addClass(affixElement, affixClass)
    return affixElement;
  }



}

import { AfterContentInit, ChangeDetectionStrategy, Component, ContentChildren, Directive, EventEmitter, forwardRef, Input, OnDestroy, Output, QueryList } from "@angular/core";
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from "@angular/forms";
import { Subscription } from "rxjs";
import { UsaRadioComponent } from "./radio.component";

/**
 * Directive to allow heading for group of radio options
 */
@Directive({
  selector: '[usaRadioGroupLabel]',
})
export class UsaRadioGroupLabel {}

@Component({
  selector: `usa-radio-group`,
  template: `
  <div role="radiogroup" [attr.aria-labelledby]="ariaLabelledBy">
    <ng-content select="usaRadioGroupLabel"></ng-content>
    <ng-content></ng-content>
  </div>`,
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => UsaRadioGroupComponent),
      multi: true,
    },
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class UsaRadioGroupComponent implements AfterContentInit, OnDestroy, ControlValueAccessor {

  /**
   * Invoked when the model has been changed
   */
  onChange: (_: any) => void;

  /**
  * Invoked when the model has been touched
  */
  onTouched: () => void;

  @ContentChildren(UsaRadioComponent) radioComponents: QueryList<UsaRadioComponent>;

  /**
   * Name for radio groups. A group of radio options should contain the same name
   */
  @Input() name: string;

  /**
   * Whether or not to display radio options as tile. This input is purely for presentation
   */
  @Input() tile: boolean;

  /**
   * Value of currently selected radio option. Each radio option within the group
   * should have a unique value. This value will match the currently selected
   * radio option's value within the group
   */
  @Input()
  get value() {
    return this._value;
  }

  set value(val: string) {
    this._value = val;
    if (!this.radioComponents) return;
    this.radioComponents.forEach(radio => {
      radio.checked = radio.value === val;
    })
  }
  _value: string;

  /**
   * Defines whether all radio options within the group are disabled or not.
   * To dispable individual radio buttons, please pass the disabled property directly
   * to the radio button
   */
  @Input()
  get disabled() {
    return this._disabled;
  }

  set disabled(value: boolean) {
    this._disabled = value;
    if (this.radioComponents) {
      this.radioComponents.forEach(radio => {
        radio.disabled = value;
      })
    }
  }
  _disabled: boolean = false;

  @Input() ariaLabelledBy: string;

  /**
   * Outputs whenever radio option changes. The output data will
   * contain two properties, target and value. Target will refer to
   * the HTMLInputElement for the selected radio input while value will
   * contain the provided value of the input
   */
  @Output() change = new EventEmitter<{target: HTMLInputElement, value: string}>();

  _subscriptions: Subscription;

  constructor() {
    this.onChange = (_:any) => {};
    this.onTouched = () => {};
    this._subscriptions = new Subscription();
  }

  ngAfterContentInit() {
    this.radioComponents.forEach(radio => {
      radio.name = radio.name ? radio.name : this.name;
      radio.tile = radio.tile ? radio.tile : this.tile;
      radio.disabled = radio.disabled != undefined ? radio.disabled : this.disabled;
      if (this.value && radio.value === this.value) {
        radio.checked = true;
      }
      this._subscribeToRadioChange(radio);
    });
  }

  ngOnDestroy() {
    if (this._subscriptions) {
      this._subscriptions.unsubscribe();
    }
  }

  writeValue(value: string): void {
    this.value = value;
    if (this.radioComponents) {
      this.radioComponents.forEach(radio => {
        radio.checked = value === radio.value;
      })
    }
  }

  registerOnChange(fn: any): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: any): void {
    this.onTouched = fn;
  }

  setDisabledState?(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  _subscribeToRadioChange(radio: UsaRadioComponent) {
    const changeSubscription = radio.change.subscribe(((change: {target: HTMLInputElement, value: string}) => {
      this.radioComponents.forEach(component => {
        component.checked = component === radio;
        component.cdr.markForCheck();
      });

      if (this.onChange && typeof(this.onChange) === 'function') {
        this.onChange(change.value);
      }

      this.change.emit(change);
    }).bind(this));
    this._subscriptions.add(changeSubscription);
  }
}

import { ChangeDetectionStrategy, ChangeDetectorRef, Component, Directive, EventEmitter, forwardRef, Input, Output } from "@angular/core";
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from "@angular/forms";

@Directive({
  selector: '[usaRadioDescription]',
