import { AfterViewInit, ContentChild, Directive, ElementRef, Input, TemplateRef } from "@angular/core";
import { UsaSort } from "./table-sort.component";


/**
 * Structural Directive to extract table header template
 * from user
 */
@Directive({
  selector: `[usaTableHeaderDef]`,
})
export class UsaTableHeaderDef {

  constructor(
    public templateRef: TemplateRef<any>,
  ) {}
}

/**
 * Directive to apply certain styles and/or attribute to table header
 */
@Directive({
  selector: `[usa-table-header]`,
  host: {
    '[attr.role]': '\'columnheader\'',
    '[attr.scope]': '\'col\'',
    '[attr.data-sortable]': 'usaSort ? true : undefined',
    '[attr.aria-sort]': 'ariaSort ? ariaSort : undefined',
    '[attr.aria-label]': 'ariaLabel'
  }
})
export class UsaTableHeader implements AfterViewInit{
  @ContentChild(UsaSort) usaSort: UsaSort;

  @Input() ariaLabel: string;

  ariaSort: 'ascending' | 'descending';

  _defaultAriaLabel: string;

  constructor(
    public elementRef: ElementRef
  ) {}

  ngAfterViewInit() {
    if (this.ariaLabel) {
      this.usaSort.setColumnHeader(this.ariaLabel);
      return;
    }

    let ariaLabel  = (this.elementRef.nativeElement as HTMLTableHeaderCellElement).innerText;
    this._defaultAriaLabel = ariaLabel;

    if (this.usaSort) {
      this.usaSort.setColumnHeader(this._defaultAriaLabel);
      ariaLabel = this._defaultAriaLabel + ', sortable column, currently unsorted'
    }

    this.ariaLabel = ariaLabel;
  }

  setSortState(sortState: 'ascending' | 'descending' | 'none') {
    if (sortState === 'none') {
      this.setSortInactive();
      return;
    }
    this.ariaSort = sortState;

    if (this._defaultAriaLabel) {
      this.ariaLabel = this._defaultAriaLabel  + ', sortable column, currently sorted ' + this.ariaSort;
    }
  }

  setSortInactive() {
    this.ariaSort = undefined;
    this.usaSort.disableSort();
    if (this._defaultAriaLabel) {
      this.ariaLabel = this._defaultAriaLabel  + ', sortable column, currently unsorted';
    }
  }
}

/**
 * Represents one row of table headers. Multiple rows of
 * table headers can exist in one table
 */
@Directive({
  selector: `[usaHeaderRowDef]`,
})
export class UsaHeaderRowDef {

  @Input() usaHeaderRowDef: string[];

  constructor(public templateRef: TemplateRef<any>) {}
}

import { Directive, Input, OnChanges, OnDestroy, OnInit } from "@angular/core";
import { Subscription } from "rxjs";
import { UsaComboBoxComponent } from "../combo-box/combo-box.component";


@Directive({
  selector: '[usa-time-picker]',
  exportAs: 'usaTimePicker'
})
export class UsaTimePicker implements OnInit, OnChanges, OnDestroy {

  readonly MAX_TIME = 60 * 24 - 1;
  readonly MIN_TIME = 0;

  readonly LABEL_FIELD = 'label';
  readonly VALUE_FIELD = 'value';

  /** 
   * Defines minimum selectable time within a 24 hour window.
   * This can be a date object or a string in hh:mm in 24-hour format.
   * The default min time is 00:00
   */
  @Input() minTime: Date | string = '00:00';

  /**
   * Defines the maximum selectable time within a 24 hour window.
   * This can be a date object or a string in hh:mm in 24-hour format.
   * The default max time is 23:59
   */
  @Input() maxTime: Date | string = '23:59';

  /**
   * The number of minutes between options. The minimum value is 1.
   * The default value is 30
   */
  @Input() timeStep: number = 30;

  /**
   * Filter function for when user types in input. This function
   * takes two parameters, current user input, and list of time
   * to parse input over. It should return a number which represents
   * the index within the values to select based on user input.
   * Index of -1 indicates no selection. A default filter function
   * is provided by the component, but a custom one can be passed in
   * if desired.
   */
  @Input() filterBy: (input: string, values: string[]) => number;

  _inputChangeSubscription: Subscription;

  constructor(
    private hostComboBox: UsaComboBoxComponent,
  ) {
    this._inputChangeSubscription = new Subscription();
  }

  ngOnChanges() {
    if (!this.hostComboBox) return;

    this.initializeDropdownItems();
  }

  ngOnInit() {
    const wrapperDiv: HTMLDivElement = this.hostComboBox.el.nativeElement.querySelector('.usa-combo-box');
    wrapperDiv.classList.add('usa-time-picker');
    this.initializeDropdownItems();

    const subscription = this.hostComboBox.changeEvent.subscribe(((value) => {
      if (!this.hostComboBox.comboBoxDropdown) return;

      const mappedItems = this.hostComboBox.items.map(item => item[this.LABEL_FIELD]);
      let index = -1;
      if (this.filterBy) {
        index = this.filterBy(value, mappedItems);
      } else {
        index = this.defaultFilter(value, mappedItems);
      }

      this.hostComboBox.comboBoxDropdown.highlightItem(index);
    }).bind(this));

    this._inputChangeSubscription.add(subscription);
  }

  ngOnDestroy() {
    this._inputChangeSubscription.unsubscribe();
  }

  /**
   * Initializes dropdown values for timepixker as well as filter function
   * to use
   */
  private initializeDropdownItems() {
    this.hostComboBox.items = this.genetateItems();
    this.hostComboBox.labelField = this.LABEL_FIELD;
    this.hostComboBox.valueField = this.VALUE_FIELD;
  }

  /**
   * Generates list of times to display to the user.
   * This list is created based on user's min/max inputs
   * as well as time step
   * @returns - list of {label, value} pair of time to display in dropdown
   */
  private genetateItems() {
    const items = [];
    const minTime = Math.max(
      this.MIN_TIME,
      this.parseTimeString(this.minTime) || this.MIN_TIME
    );
    const maxTime = Math.min(
      this.MAX_TIME,
      this.parseTimeString(this.maxTime) || this.MAX_TIME
    );

    for (let time = minTime; time <= maxTime; time += this.timeStep) {
      const { minute, hour24, hour12, ampm } = this.getTimeContext(time);
      const option = {};
      option[this.VALUE_FIELD] = `${this.padZeros(hour24, 2)}:${this.padZeros(minute, 2)}`;
      option[this.LABEL_FIELD] = `${hour12}:${this.padZeros(minute, 2)}${ampm}`;

      items.push(option);
    }

    return items;
  }

  /**
   * Adds additional zeros if needed
   * @param value 
   * @param length 
   * @returns 
   */
  private padZeros(value: number, length: number) {
    return `0000${value}`.slice(-length);
  }

  /**
   * Given minuutes, parses number of hours and minutes to represent that time.
   * IE - 0 minutes would be 0:00 AM, 60 minutes would be 1:00 AM, etc
   * @param minutes 
   */
  private getTimeContext(minutes: number): {minute: number, hour24: number, hour12: number, ampm: string} {
    const minute = minutes % 60;
    const hour24 = Math.floor(minutes / 60);
    const hour12 = hour24 % 12 || 12;
    const ampm = hour24 < 12 ? "am" : "pm";

    return {
      minute,
      hour24,
      hour12,
      ampm,
    };
  };

  /**
   * Parse a string of hh:mm into minutes
   *
   * @param {string} timeStr the time string to parse
   * @returns {number} the number of minutes
   */
  private parseTimeString(timeStr) {
    let minutes;

    if (timeStr) {
      const [hours, mins] = timeStr.split(":").map((str) => {
        let value;
        const parsed = parseInt(str, 10);
        if (!Number.isNaN(parsed)) value = parsed;
        return value;
      });

      if (hours != null && mins != null) {
        minutes = hours * 60 + mins;
      }
    }

    return minutes;
  };

  /**
   * Default filter search if one is not provided. Used to find
   * time within dropdown values that best matches user input
   * @param input - user input
   * @param values - list of values to search
   * @returns - index in values list if match is found, -1 otherwise
   */
  defaultFilter(input: string, values: string[]) {
    if (!input || !input.length) return -1;
    return values.findIndex(value => value.includes(input));
  }
}

import {
  AfterViewInit,
  Directive,
  ElementRef,
  Input,
  Renderer2,
} from '@angular/core';

@Directive({
  selector: '[usaTooltip]',
  host: {
    class: 'usa-tooltip__trigger',
    '(mouseenter)': 'show()',
    '(focus)': 'show()',
    '(mouseleave)': 'hide()',
    '(blur)': 'hide()',
  },
})
export class UsaTooltipDirective implements AfterViewInit {
  // Span which contains the tooltip text
  tooltipBody: HTMLElement;
  // Element which causes tooltip to be shown, tooltip apppears relative to this
  tooltipTrigger: HTMLElement;
  // Element which wraps tooltip and trigger
  tooltipWrapper: HTMLElement;

  readonly TRIANGLE_SIZE = 5;

  @Input()
  position: 'top' | 'bottom' | 'left' | 'right' = 'top';

  /**
   * Value to be displayed in tooltip
   */
  @Input()
  title: string;

  /**
   * Additional classes to be applied to wrapper
   */
  @Input()
  classes: string;

  constructor(private el: ElementRef, private renderer: Renderer2) {}

  ngAfterViewInit() {
    const tooltipID = `tooltip-${Math.floor(Math.random() * 900000) + 100000}`;

    this.tooltipTrigger = this.el.nativeElement;
    this.tooltipWrapper = document.createElement('span');
    this.tooltipBody = document.createElement('span');

    this.tooltipTrigger.setAttribute('aria-describedby', tooltipID);
    this.tooltipTrigger.setAttribute('tabindex', '0');
    this.tooltipTrigger.classList.remove('usa-tooltip');

    const parent = this.renderer.parentNode(this.el.nativeElement);
    parent.replaceChild(this.tooltipWrapper, this.el.nativeElement);
    this.renderer.appendChild(this.tooltipWrapper, this.tooltipTrigger);
    this.renderer.appendChild(this.tooltipWrapper, this.tooltipBody);
    this.renderer.addClass(this.tooltipWrapper, 'usa-tooltip');

    if (this.classes) {
      this.classes
        .split(' ')
        .forEach((className) => this.tooltipWrapper.classList.add(className));
    }

    this.renderer.addClass(this.tooltipBody, 'usa-tooltip__body');
    this.renderer.setAttribute(this.tooltipBody, 'id', tooltipID);
    this.renderer.setAttribute(this.tooltipBody, 'role', 'tooltip');
    this.renderer.setAttribute(this.tooltipBody, 'aria-hidden', 'true');

    const text = this.renderer.createText(this.title);
    this.renderer.appendChild(this.tooltipBody, text);
  }

  positionTop(): void {
    this.resetPositionStyles(this.tooltipBody);

    const top = this.calculateMarginOffset(
      'top',
      this.tooltipBody.offsetHeight,
      this.tooltipTrigger
    );
    const left = this.calculateMarginOffset(
      'left',
      this.tooltipBody.offsetWidth,
      this.tooltipTrigger
    );

    this.setPositionClass('top');

    this.tooltipBody.style.left = '50%';
    this.tooltipBody.style.top = `-${this.TRIANGLE_SIZE}px`;
    this.tooltipBody.style.margin = `-${top}px 0 0 -${left / 2}px`;
  }

  positionBottom(): void {
    this.resetPositionStyles(this.tooltipBody);

    const left = this.calculateMarginOffset(
      'left',
      this.tooltipBody.offsetWidth,
      this.tooltipTrigger
    );

    this.setPositionClass('bottom');

    this.tooltipBody.style.left = '50%';
    this.tooltipBody.style.margin = `${this.TRIANGLE_SIZE}px 0 0 -${
      left / 2
    }px`;
  }

  positionLeft(): void {
    this.resetPositionStyles(this.tooltipBody);

    const top = this.calculateMarginOffset(
      'top',
      this.tooltipBody.offsetHeight,
      this.tooltipTrigger
    );
    const left = this.calculateMarginOffset(
      'left',
      this.tooltipTrigger.offsetLeft > this.tooltipBody.offsetWidth
        ? this.tooltipTrigger.offsetLeft - this.tooltipBody.offsetWidth
        : this.tooltipBody.offsetWidth,
      this.tooltipTrigger
    );

    this.setPositionClass('left');

    this.tooltipBody.style.top = `50%`;
    this.tooltipBody.style.left = `-${this.TRIANGLE_SIZE}px`;
    this.tooltipBody.style.margin = `-${top / 2}px 0 0 ${
      this.tooltipTrigger.offsetLeft > this.tooltipBody.offsetWidth
        ? left
        : -left
    }px`;
  }
  positionRight(): void {
    this.resetPositionStyles(this.tooltipBody);

    const top = this.calculateMarginOffset(
      'top',
      this.tooltipBody.offsetHeight,
      this.tooltipTrigger
    );

    this.setPositionClass('right');

    this.tooltipBody.style.top = '50%';
    this.tooltipBody.style.left = `${
      this.tooltipTrigger.offsetLeft +
      this.tooltipTrigger.offsetWidth +
      this.TRIANGLE_SIZE
    }px`;
    this.tooltipBody.style.margin = `-${top / 2}px 0 0 0`;
  }

  setPositionClass(position: string): void {
    this.renderer.removeClass(this.tooltipBody, 'usa-tooltip__body--top');
    this.renderer.removeClass(this.tooltipBody, 'usa-tooltip__body--bottom');
    this.renderer.removeClass(this.tooltipBody, 'usa-tooltip__body--left');
    this.renderer.removeClass(this.tooltipBody, 'usa-tooltip__body--right');
    this.renderer.addClass(this.tooltipBody, `usa-tooltip__body--${position}`);
  }

  show() {
    this.tooltipBody.setAttribute('aria-hidden', 'false');
    this.renderer.addClass(this.tooltipBody, 'is-set');
    this.renderer.addClass(this.tooltipBody, 'is-visible');

    switch (this.position) {
      case 'top': {
        this.positionTop();
        if (!this.isInViewport(this.tooltipBody)) {
          this.positionBottom();
        }
        break;
      }
      case 'bottom': {
        this.positionBottom();
        if (!this.isInViewport(this.tooltipBody)) {
          this.positionTop();
        }
        break;
      }
      case 'left':
        this.positionLeft();
        if (!this.isInViewport(this.tooltipBody)) {
          this.positionRight();
          if (!this.isInViewport(this.tooltipBody)) {
            this.positionTop();
          }
        }
        break;
      case 'right':
        this.positionRight();
        if (!this.isInViewport(this.tooltipBody)) {
          this.positionLeft();
          if (!this.isInViewport(this.tooltipBody)) {
            this.positionTop();
          }
        }
        break;
    }
  }

  hide() {
    this.renderer.removeClass(this.tooltipBody, 'is-set');
    this.renderer.removeClass(this.tooltipBody, 'is-visible');
    this.tooltipBody.setAttribute('aria-hidden', 'true');
  }

  isInViewport(elementToCheck): boolean {
    const rect = elementToCheck.getBoundingClientRect();
    return (
      rect.top >= 0 &&
      rect.left >= 0 &&
      rect.bottom <=
        (window.innerHeight || document.documentElement.clientHeight) &&
      rect.right <= (window.innerWidth || document.documentElement.clientWidth)
    );
  }

  offsetMargin(target, propertyToCheck): number {
    return parseInt(
      window.getComputedStyle(target).getPropertyValue(propertyToCheck),
      10
    );
  }

  calculateMarginOffset(positionToCheck, bodyOffset, trigger) {
    return this.offsetMargin(trigger, `margin-${positionToCheck}`) > 0
      ? bodyOffset - this.offsetMargin(trigger, `margin-${positionToCheck}`)
      : bodyOffset;
  }

  resetPositionStyles(target): void {
    target.style.top = null;
    target.style.bottom = null;
    target.style.right = null;
    target.style.left = null;
    target.style.margin = null;
  }
}

