import { HttpResponse } from '@angular/common/http';
import { Component } from '@angular/core';
import { Observable } from 'rxjs';

@Component({
  selector: 'file-input-upload',
  templateUrl: './file-input-upload.component.html',
  styles: [
  ]
})
export class FileInputUploadComponent {

  simulateServerDelay = false;
  serverResponseSucceeds = true;
  uploadFilesMethod = this.uploadFilesSuccess;
  failedUploads: File[] = [];

  onFilesChange($event) {
    this.failedUploads = [];
  }

  onToggleServerSideResponse() {
    this.serverResponseSucceeds = !this.serverResponseSucceeds;
    this.uploadFilesMethod = this.serverResponseSucceeds ? this.uploadFilesSuccess : this.uploadFilesFail;
  }

  /**
   * Simulate case where server endpoint returns HTTP OK after requesting file upload
   * @param file 
   * @returns 
   */
  uploadFilesSuccess(file: File) {
    return new Observable((observer) => {
      setTimeout(() => {
        observer.next(new HttpResponse({ status: 200 }));
        observer.complete();
      }, 3000);
    });
  }

  /**
   * Simulate case where server endpoint returns HTTP ERROR after requesting file upload
   * @param file 
   * @returns 
   */
  uploadFilesFail(file: File) {
    return new Observable((observer) => {
      setTimeout(() => {
        observer.error(new HttpResponse({ status: 400 }));
        observer.complete();
      }, 3000);
    });
  }

  /**
   * Event fired when upload fails
   * @param file 
   */
  onUploadError(file: File) {
    this.failedUploads.push(file);
  }
}

import { Component, Input, ChangeDetectorRef } from '@angular/core';
import { NgControl } from '@angular/forms';

@Component({
  selector: 'usa-form-field',
  templateUrl: 'form-field.component.html',
})
export class FormFieldComponent {
  /**
   * sets the label text
   */
  @Input() public label: string;
  /**
   * sets the id attribute value
   */
  @Input() public id: string;
  /**
   * sets the description text
   */
  @Input() public description: string;
  /**
   * deprecated, toggles the required text
   */
  @Input() public required: boolean = false;
  /**
   * toggles the required text
   */
  @Input() public requiredFlag: boolean = false;

  /**
   * set the error message
   */
  @Input() public set errorMessage(message: string) {
    this._errorMessage = message;
  }

  public get errorMessage(): string {
    return this._errorMessage;
  }

  public input: HTMLElement;

  public errorElId: string;
  public descriptionElId: string;

  private _errorMessage = '';

  constructor(private cdr: ChangeDetectorRef) {}

  public formatErrors(control: NgControl) {
    if (!control) {
      return;
    }

    if (control.pristine || !control.errors) {
      this.errorMessage = '';
      return;
    }

    if (control.invalid && control.errors) {
      for (const k in control.errors) {
        const errorObject = control.errors[k];

        // to check if it's not null & is object
        if (
          errorObject.message !== null &&
          typeof errorObject.message === 'object'
        ) {
          this.errorMessage = errorObject.message;
          return;
        }
        this.setInvalidErrors(k, errorObject);
      }
    }
  }

  public clearError() {
    this.errorMessage = '';
  }

  private setInvalidErrors(error, errorObject) {
    const actualLength = errorObject.actualLength;
    const requiredLength = errorObject.requiredLength;

    switch (error) {
      case 'minlength':
        this.errorMessage = `The number of characters should not be less than ${requiredLength}`;
        return;
      case 'maxlength':
        this.errorMessage =
          actualLength +
          ' characters input but max length is ' +
          requiredLength;
        return;
      case 'required':
        this.errorMessage = 'This field is required';
        return;
      case 'isNotBeforeToday':
        this.errorMessage = 'Date must not be before today';
        return;
    }
  }
}

