    return this._dateAdapter
      .format(this.calendar.activeDate, this._dateFormats.display.monthLabel)
      .toLocaleUpperCase();
  }

  get yearLabel(): string {
    return this._dateAdapter.getYearName(this.calendar.activeDate);
  }

