    private _changeDetectorRef: ChangeDetectorRef,
    private _globalModel: UsaDateSelectionModel<S, D>,
    private _dateAdapter: DateAdapter<D>,
    private _zone: NgZone,
    private _el: ElementRef,
    @Optional()
