import { 
  Component, 
  ElementRef, 
  Input, 
  TemplateRef, 
  ViewChild, 
  forwardRef,
  Output,
  EventEmitter, 
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { Observable } from 'rxjs';
import { FileInputConfig } from './file-input.config';

export interface UploadedFile {
  isLoading?: boolean, 
  imageId: string, 
  file: File
}
@Component({
  selector: 'usa-file-input',
  templateUrl: './file-input.component.html',
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => UsaFileInputComponent),
      multi: true,
    },
  ],
})
export class UsaFileInputComponent implements ControlValueAccessor {

  @ViewChild('fileInputEl') fileInputElement: ElementRef;

  @Input() multiple = false;

  @Input() acceptFileType: string = '';

  @Input() id: string;

  @Input() hint: string | TemplateRef<any>;

  @Input() disabled: boolean;

  @Input() selectedFiles: File[] = [];

  @Input() clearFilesOnAdd: boolean;

  @Input() displayFileInfo: boolean;

  @Input() uploadRequest: (file: File) => Observable<any>;

  @Output() selectedFilesChange = new EventEmitter<File[]>();

  @Output() uploadError = new EventEmitter<File>();

  DRAG_OVER_STATE = false;
  ERROR_STATE = false;

  inputFiles: UploadedFile[] = [];

  // Save the callbacks, make sure to have a default so your app
  // doesn't crash when one isn't (yet) registered
  private onChange = (v: any) => {};
  private onTouched = () => {};


  constructor(
    private fileInputConfig: FileInputConfig,
  ) { 
    this.multiple = this.fileInputConfig.multiple;
    this.acceptFileType = this.fileInputConfig.acceptFileType;
    this.clearFilesOnAdd = this.fileInputConfig.clearFilesOnAdd;
    this.displayFileInfo = this.fileInputConfig.displayFileInfo;
    this.uploadRequest = this.fileInputConfig.uploadRequest;
  }

  onDragLeave() {
    this.DRAG_OVER_STATE = false;
  }

  onDragOver() {
    this.DRAG_OVER_STATE = true;
  }

  onFileDrop() {
    this.DRAG_OVER_STATE = false;
  }

  onNewFilesUpload($event) {
    const newFiles: File[] = Array.from($event.target.files);
    this.fileInputElement.nativeElement.value = null;

    if (newFiles.length === 0) {
      return;
    }

    // Validate new files match expected types
    let areValidFiles = this.validateFileType(newFiles);

    if (areValidFiles) {
      this.ERROR_STATE = false;
    } else {
      this.ERROR_STATE = true;
      this.selectedFiles = [];
      this.inputFiles = [];
      return;
    }

    // Clear current files OR append to existing based on user config
    if (!this.multiple || this.clearFilesOnAdd) {
      this.inputFiles = [];
      this.selectedFiles = newFiles;
    } else {
      this.selectedFiles = this.selectedFiles.concat(newFiles);
    }

    // Read file data and add laoding / preview states
    newFiles.forEach(file => {
      const imageId = this.getImageId(file, this.inputFiles);
      const inputFile = {
        isLoading: true, imageId, file
      };
      this.inputFiles.push(inputFile);
    });

    this.selectedFilesChange.emit(this.selectedFiles);

    // Notify Angular to update its model
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  removeFile(file: File) {
    const index = this.selectedFiles.indexOf(file);
    let inputFileIndex = -1;
    this.inputFiles.forEach((inputFile, index) => {
      if (inputFile.file === file) {
        inputFileIndex = index;
      }
    });

    if (index === -1 || inputFileIndex === -1) {
      return;
    }

    this.selectedFiles.splice(index, 1);
    this.selectedFilesChange.emit(this.selectedFiles);
    this.writeValue(this.selectedFiles);
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  clearFiles() {
    this.selectedFiles = [];
    this.selectedFilesChange.emit(this.selectedFiles);
    this.writeValue(this.selectedFiles);
    this.onChange(this.selectedFiles);
    this.onTouched();
  }

  onUploadError(file: File) {
    this.uploadError.emit(file);
  }

  trackLoadedFilesBy(index: number, item: UploadedFile) {
    return item.imageId;
  }

  registerOnChange(fn: any): void {
    this.onChange = fn;
  }
  registerOnTouched(fn: any): void {
    this.onTouched = fn;
  }

  writeValue(files: File[]): void {
    if (this.disabled || !files) {
      return;
    }
    let areValidFiles = this.validateFileType(files);

    if (areValidFiles) {
      this.ERROR_STATE = false;
    } else {
      this.ERROR_STATE = true;
      return;
    }

    this.inputFiles = [];
    this.selectedFiles = files;
    // Read file data and add laoding / preview states
    files.forEach(file => {
      const imageId = this.getImageId(file, this.inputFiles);
      const inputFile = {
        isLoading: true, imageId, file
      };
      this.inputFiles.push(inputFile);
    });
  }

  setDisabledState(isDisabled: boolean) {
    this.disabled = isDisabled;
  }

  private validateFileType(files: File[]) {
    if (!this.acceptFileType) {
      return true;
    }

    const acceptedFiles = this.acceptFileType.split(',');

    for(let i = 0; i < files.length; i++) {
      const isValidFileType = acceptedFiles.some(acceptedFileType => {
        const endsWithFileType = new RegExp(acceptedFileType + '$', 'i').test(files[i].name);
        return endsWithFileType || files[i].type.includes(acceptedFileType.replace(/\*/g, ""))
      });

      if (!isValidFileType) {
        return false;
      }
    }

    return true;
  }



  /**
   * Given a file and list of pre-uploaded files, generates and returns an id for the file.
   * If file already exists in pre-uploaded files list, then the id will simply be appended with
   * a counter to ensure uniqueness
   * @param file - File to generate id for
   * @param preExistingFiles - List of files that are uploaded and already have an id
   * @returns An id for the image
   */
  private getImageId(file: File, preUploadedFiles: UploadedFile[]) {
    let imageId = this._makeSafeForID(file.name);

    imageId = `a${imageId}`;
    let numExisting = 0;
    preUploadedFiles.forEach(file => {
      if (file.imageId === imageId) {
        numExisting++;
      }
    });

    if (numExisting > 0) {
      imageId = `${imageId}${numExisting}`;
    }
    return imageId;
  }

  /**
   * Creates an ID name for each file that strips all invalid characters.
   * @param {string} name - name of the file added to file input
   * @returns {string} same characters as the name with invalid chars removed
   */
  private _makeSafeForID(name: string) {
    return name.replace(/[^a-z0-9]/g, function replaceName(s) {
      const c = s.charCodeAt(0);
      if (c === 32) return "-";
      if (c >= 65 && c <= 90) return `img_${s.toLowerCase()}`;
      return `__000${(c.toString(16)).slice(-4)}`;
    });
  }
}

import {
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  Component,
  ContentChild,
  ElementRef,
  EventEmitter,
  HostListener,
  Input,
  OnInit,
  Output,
  TemplateRef,
  ViewChild,
} from '@angular/core';
import {
  UsaHeaderPrimaryLinkTemplate,
  UsaHeaderSecondaryLinkTemplate,
} from './header-selectors';
import { UsaHeaderPrimaryLink } from './header.model';
import { UsaNavigationLink, UsaNavigationMode } from '../util/navigation';
@Component({
  selector: 'usa-header',
  templateUrl: './header.component.html',
  changeDetection: ChangeDetectionStrategy.OnPush,
})
export class UsaHeaderComponent implements OnInit {
  @ContentChild(UsaHeaderPrimaryLinkTemplate)
  primaryLinkTemplate: TemplateRef<any>;
  @ContentChild(UsaHeaderSecondaryLinkTemplate)
  secondaryLinkTemplate: TemplateRef<any>;

  @ViewChild('usaNavOpen') openNavBtn: ElementRef;
  @ViewChild('usaNavClose') closeNavBtn: ElementRef;

  mobileNavActive = false;

  /**
   * Title to display in header. Can be a string or a template reference
   */
  @Input() title: string | TemplateRef<any>;

  /**
   * Template to use for displaying menu button on mobile mode. By default,
   * the text 'Menu' will be used
   */
  @Input() menuButtonTemplate: TemplateRef<any>;

  /**
   * Aria label to use for over all navigation content
   * @default 'Primary Navigation'
   */
  @Input() navAriaLabel = 'Primary Navigation';

  /**
   * Whether the header should have secondary links or not.
   * @default false
   */
  @Input() extended = false;

  /**
   * Primary navigation items to display for header
   */
  @Input() primaryNavItems: UsaHeaderPrimaryLink[];

  /**
   * Secondary navigation items to display for header. Please note
   * that `extended` input must also be passed in as `true` for the
   * secondary nav items to display
   */
  @Input() secondaryNavItems: UsaNavigationLink[];

  /**
   * Defines whether or not to display dark overlay in background
   * whenever submenu or megamenu is active
   */
  @Input() displayOverlayOnMenuOpen = false;

  /**
   * event emitted whenever a navigation item is clicked.
   * This only fires if navigation mode for the link event is NONE or undefined
   */
  @Output()
  linkEvent = new EventEmitter<UsaNavigationLink>();

  NavigationMode = UsaNavigationMode;

  /** Reference of link whose dropdown menu is currently open */
  selectedDropdownLink: UsaHeaderPrimaryLink;

  /** Reference to currently selected navigation link */
  selectedNavItem: UsaNavigationLink;

  // When the mobile nav is active, and the close box isn't visible,
  // we know the user's viewport has been resized to be larger.
  // Let's make the page state consistent by deactivating the mobile nav.
  @HostListener('window:resize', ['$event'])
  onBrowserResize(event) {
    if (
      this.mobileNavActive &&
      this.closeNavBtn.nativeElement.getBoundingClientRect().width === 0
    ) {
      this.mobileNavActive = false;
    }
  }

  @HostListener('document:click', ['$event'])
  documentClick($event: any) {
    this.selectedDropdownLink = null;
  }

  constructor(public changeDetector: ChangeDetectorRef) {}

  ngOnInit() {
    /** Look through primary and secondary nav and select the first selected nav item.
     * Toggle the rest off if they are selected */
    this.selectInitialNavItem(this.primaryNavItems);
    this.selectInitialNavItem(this.secondaryNavItems);
  }

  getTypeOfTitle(): 'string' | 'object' {
    return typeof this.title === 'string' ? 'string' : 'object';
  }

  /**
   * Takes in a text string and removes all white space characters and returns the new string
   * @param text
   */
  removeWhiteSpace(text: string) {
    return text.replace(/ /g, '');
  }

  /**
   * Link clicked and emits the link data into an event
   * @param link
   */
  linkClickEvent(link: UsaNavigationLink, parentNav?: UsaNavigationLink) {
    const itemToSelect = parentNav ? parentNav : link;
    this.selectNavItem(itemToSelect);
    this.linkEvent.emit(link);
  }

  /**
   * Public Interface - select a navigation item to mark as selected.
   * @param item - The item to select. This item will be marked as selected, and
   * any previously selected item will be un-selected. Note that only one nav item may
   * be selected at a time
   */
  selectNavItem(item: UsaNavigationLink) {
    if (this.selectedNavItem) {
      this.selectedNavItem.selected = false;
    }

    this.selectedNavItem = item;
    this.selectedNavItem.selected = true;
    this.changeDetector.detectChanges();
  }

  dropdownLinkClicked(link: UsaHeaderPrimaryLink, $event: Event) {
    // Opened dropdown link was clicked, close it
    if (link === this.selectedDropdownLink) {
      this.selectedDropdownLink = null;
      return;
    }

    // Otherwise, set the newly clicked link as the new open dropdown
    this.selectedDropdownLink = link;
    $event?.stopImmediatePropagation();
  }

  openMobileNav() {
    this.mobileNavActive = true;
  }

  closeMobileNav() {
    this.mobileNavActive = false;
    this.openNavBtn.nativeElement.focus();
  }

  navAnimationEnd() {
    this.closeNavBtn.nativeElement.focus();
  }

  /** Used during component init - selects the initial nav item by looking through input nav items */
  private selectInitialNavItem(navItemsList: UsaNavigationLink[]) {
    if (!navItemsList) return;

    navItemsList.forEach((item) => {
      if (this.selectedNavItem) {
        item.selected = false;
      } else if (item.selected) {
        this.selectedNavItem = item;
      }
    });
  }
}

import { 
  Component, ElementRef, EventEmitter, HostListener, 
  Input, Output, TemplateRef, ViewChild, ViewContainerRef, ViewRef 
} from "@angular/core";

let submenuId = 0;

@Component({
  selector: `button[usaHeaderSubmenu]`,
  host: {
    class: `usa-accordion__button usa-nav__link`,
    '[attr.aria-expanded]': 'selected',
    '[attr.aria-controls]': 'id', 
    '(click)': 'onClick()',
  },
  template: `
    <span><ng-content></ng-content></span>
    <ng-template #submenuView>
      <div class="usa-nav__submenu" [ngClass]="{'usa-megamenu': isMegamenu}">
        <ng-container [ngTemplateOutlet]="content"></ng-container>
      </div>
    </ng-template>
  `
})
export class UsaHeaderSubmenuButton {
  @ViewChild('submenuView') submenuTemplate: TemplateRef<any>;

  /**
   * Required Input - template reference of content to display within the megamenu
   */
  @Input() content: TemplateRef<any>;

  /**
   * Defines whether the content should be displayed as a megamenu rather than a menu.
   * A megamenu will open a menu that expands the entire width of the page
   * @default false
   */
  @Input() isMegamenu: boolean = false;

  /**
   * Whether the megamenu is open initially or not
   * @default - false
   */
  @Input() selected = false;

  /**
   * Id for megamenu section
   */
  @Input() id = `usa-megamenu-nav-${submenuId++}`;

  /**
   * Output event emitted whenever the state of megamenu (opened or closed) changes.
   */
  @Output() selectedChange = new EventEmitter<boolean>();

  /**
   * Listener for click events anywhere in the DOM. If the click event was outside
   * this submenu element, then close the submeny.
   * @param $event - click event
   */
  @HostListener('document:click', ['$event'])
  onDocumentClick($event) {
    if (!this.selected || this.el.nativeElement.contains($event.target)) {
      return;
    }

    this.selected = false;
    this.detachContent();

    this.selectedChange.emit(this.selected);
  }

  private _attachedViewRef: ViewRef;

  constructor(
    private el: ElementRef,
    private viewContainer: ViewContainerRef,
  ) {}

  /**
   * Toggles selected value when button is clicked. If the item is selected, then we should
   * display the submenu. If the item is un-selected, then we should remove the submenu
   */
  onClick() {
    this.selected = !this.selected;
    this.selected ? this.attachContent() : this.detachContent();
    this.selectedChange.emit(this.selected);
  }

  /**
   * Attaches the submenu div as next sibling to button
   */
  attachContent() {
    this._attachedViewRef = this.viewContainer.createEmbeddedView(this.submenuTemplate);
  }

  /**
   * Removes dropdown content and detaches the created view container
   */
  detachContent() {
    this._attachedViewRef.destroy();
  }
}

import { Component, EventEmitter, Input, Output } from "@angular/core";
import { UsaNavigationLink, UsaNavigationMode } from "../../util/navigation";


@Component({
  selector: `usa-link-template`,
  templateUrl: './link-template.component.html'
})
export class UsaLinkTemplateComponent {

  NavigationMode = UsaNavigationMode;

  @Input() link: UsaNavigationLink;

  /**
   * Class to apply to anchor tag element when
   * displaying link.
   * @default - empty string
   */
  @Input() class: string = '';

  /** 
   * Class to apply for selected item. 
   * Use empty string to not apply any class for selected item
   * @default 'usa-current'
   * */
  @Input() currentClass: string = 'usa-current';


  @Output() linkClicked = new EventEmitter<UsaNavigationLink>();

  /**
   * creates url from provided route and query params
   * @param item - Link to use when building url
   */
  urlBuilder(item: UsaNavigationLink): string {
    let url = item.path;
    const queryParams = this.queryStringBuilder(item);
    if (queryParams) {
      if (url.indexOf('?') === -1) {
        url += '?' + queryParams;
      } else if (url.indexOf('?') === url.length - 1) {
        url += queryParams;
      } else {
        url += '&' + queryParams;
      }
    }
    return url;
  }

  private queryStringBuilder(item: UsaNavigationLink): string {
    const ret = [];
    let keys = [];
    if (item.queryParams) {
      keys = Object.keys(item.queryParams);
    }
    for (const d of keys) {
      ret.push(encodeURIComponent(d) + '=' + encodeURIComponent(item.queryParams[d]));
    }
    return ret.join('&');
  }
}

import {DOCUMENT} from '@angular/common';
import {
  AfterViewInit,
  Component,
  ElementRef,
  EventEmitter,
  Inject,
  Input,
  NgZone,
  OnDestroy,
  OnInit,
  Output,
  ViewChild,
  ViewEncapsulation
} from '@angular/core';

import {fromEvent, Observable, Subject} from 'rxjs';
import {filter, switchMap, take, takeUntil, tap} from 'rxjs/operators';

import {getFocusableBoundaryElements} from '../util/focus-trap';
import { Key, KeyCode, MicrosfotKeys} from '../util/key';
import {ModalDismissReasons} from './modal-dismiss-reasons';
import { usaDialogAnimations } from './modal-animations';
import { AnimationEvent } from '@angular/animations';

let nextId = 0;
@Component({
  selector: 'usa-modal-window',
  animations: [usaDialogAnimations.dialogContainer],
  host: {
    '[class]': '"usa-modal" + (modalDialogClass ? " " + modalDialogClass : "")',
    '[class.usa-modal--lg]': 'size === \'lg\'',
    'role': 'dialog',
    'tabindex': '-1',
    '[attr.id]': 'id',
    '[attr.aria-modal]': 'true',
    '[attr.aria-labelledby]': 'ariaLabelledBy',
    '[attr.aria-describedby]': 'ariaDescribedBy',
    '[@.disabled]': '!animation',
    '[@dialogContainer]': '_state',
    '(@dialogContainer.done)': '_onAnimationDone($event)',
  },
  template: `
    <div #dialog class="usa-modal__content">
      <div class="usa-modal__main">
        <ng-content></ng-content>
      </div>

      <button *ngIf="showClose" class="usa-button usa-modal__close" aria-label="Close this window" (click)="onCloseClicked()">
        <svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24" class="usa-icon">
          <path d="M0 0h24v24H0z" fill="none"/>
          <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
        </svg>
      </button>
    </div>
    `,
  encapsulation: ViewEncapsulation.None,
})
export class UsaModalWindow implements OnInit,
    AfterViewInit, OnDestroy {
  private _closed$ = new Subject<void>();
  private _elWithFocus: Element | null = null;  // element that is focused prior to modal opening

  /** State of the dialog animation. */
  _state: 'void' | 'enter' | 'slideEnter' | 'exit' | 'slideExit';

  @ViewChild('dialog', {static: true}) private _dialogEl: ElementRef<HTMLElement>;

  @Input() animation: boolean;
  @Input() ariaLabelledBy: string;
  @Input() ariaDescribedBy: string;
  @Input() backdrop: boolean | string = true;
  @Input() keyboard = true;
  @Input() size: string;
  @Input() modalDialogClass: string;
  @Input() overlayElement: Element;
  @Input() showClose: boolean = true;
  @Input() id: string = `usa-modal-${nextId++}`;

  @Output('dismiss') dismissEvent = new EventEmitter();

  shown = new Subject<void>();
  hidden = new Subject<void>();

  _animationStateChanged = new Subject<void>();

  constructor(
      @Inject(DOCUMENT) private _document: any,
      private _elRef: ElementRef<HTMLElement>,
      private _zone: NgZone,
    ) {}

  dismiss(reason): void { this.dismissEvent.emit(reason); }

  ngOnInit() {
    this._elWithFocus = this._document.activeElement;
    this._state = this.animation ? 'slideEnter' : 'enter';
  }

  ngAfterViewInit() { this._show(); }

  ngOnDestroy() { this._disableEventHandling(); }

  hide(): Observable<any> {

    const exitAnimation$ = this._startExitAnimation();
    exitAnimation$.subscribe(() => {
      this.hidden.next();
      this.hidden.complete();
    });

    this._disableEventHandling();
    this._restoreFocus();
    return exitAnimation$;
  }

  onCloseClicked() {
    this.dismiss(ModalDismissReasons.CLOSE_CLICKED);
  }

  private _show() {
    this._enableEventHandling();
    this._setFocus();
  }

  private _enableEventHandling() {
    const {nativeElement} = this._elRef;
    this._zone.runOutsideAngular(() => {
      fromEvent<KeyboardEvent>(nativeElement, 'keydown')
          .pipe(
              takeUntil(this._closed$),
              filter(e => e.key === Key.Escape || e.key === MicrosfotKeys.Escape || e.which === KeyCode.Escape))
          .subscribe(event => {
            if (this.keyboard) {
              requestAnimationFrame(() => {
                if (!event.defaultPrevented) {
                  this._zone.run(() => this.dismiss(ModalDismissReasons.ESC));
                }
              });
            }
          });

      let preventClose = false;
      fromEvent<MouseEvent>(this._dialogEl.nativeElement, 'mousedown')
          .pipe(
              takeUntil(this._closed$), tap(() => preventClose = false),
              switchMap(() => fromEvent<MouseEvent>(nativeElement, 'mouseup').pipe(takeUntil(this._closed$), take(1))),
              filter(({target}) => nativeElement === target))
          .subscribe(() => { preventClose = true; });

      fromEvent<MouseEvent>(this.overlayElement, 'click').pipe(takeUntil(this._closed$)).subscribe(({target}) => {
        if (this.overlayElement === target) {
          if (this.backdrop === true && !preventClose) {
            this._zone.run(() => this.dismiss(ModalDismissReasons.BACKDROP_CLICK));
          }
        }

        preventClose = false;
      });
    });
  }

  private _disableEventHandling() { this._closed$.next(); }

  private _setFocus() {
    const {nativeElement} = this._elRef;
    if (!nativeElement.contains(document.activeElement)) {
      const autoFocusable = nativeElement.querySelector(`[usaAutofocus]`) as HTMLElement;
      const firstFocusable = getFocusableBoundaryElements(nativeElement)[0];

      const elementToFocus = autoFocusable || firstFocusable || nativeElement;
      elementToFocus.focus();
    }
  }

  private _restoreFocus() {
    const body = this._document.body;
    const elWithFocus = this._elWithFocus;

    let elementToFocus;
    if (elWithFocus && elWithFocus['focus'] && body.contains(elWithFocus)) {
      elementToFocus = elWithFocus;
    } else {
      elementToFocus = body;
    }
    this._zone.runOutsideAngular(() => {
      setTimeout(() => elementToFocus.focus());
      this._elWithFocus = null;
    });
  }

  private _startExitAnimation() {
    this._state = this.animation ? 'slideExit' : 'exit';
    return this._animationStateChanged.asObservable();
  }

  /** Callback, invoked whenever an animation on the host completes. */
  private _onAnimationDone(event: AnimationEvent) {
    if (event.toState === 'exit' || event.toState === 'slideExit') {
      this._animationStateChanged.next();
      this._animationStateChanged.complete();
    }

    if (event.toState === 'enter' || event.toState === 'slideEnter') {
      this.shown.next();
      this.shown.complete();
    }
  }
}

